INSERT INTO `articles` (`id`, `title`, `summary`, `content`, `alias`, `image`, `category_id`, `author`, `created_at`, `updated_at`, `status`, `creator_id`, `created_date`) VALUES
(1, 'Phó Thủ tướng: Có quản lý thị trường "bảo kê" cho hàng giả!', 'Phó Thủ tướng Chính phủ Nguyễn Xuân Phúc đánh giá cao những thành tích đạt được của lực lượng Quản lý thị trường cả nước trong năm 2015. Tuy nhiên, một bộ phận của QLTT có tình trạng kiểm tra nhiều nhưng xử lý ít, còn gây phiền hà, bảo kê, tiếp tay cho các tiêu cực…', 'Trong năm 2015, lực lượng quản lý thị trường (QLTT) trên cả nước đã kiểm tra hơn 174.000 vụ (tăng hơn 5.400 vụ, tăng 3,2% so với năm 2014); phát hiện, xử lý hơn 103.700 vụ vi phạm (tăng gần 10.500 vụ, tăng 11,2% so với năm 2014); tổng số thu nộp ngân sách gần 460 tỷ đồng (tăng 63,5 tỷ, tăng 16% so với năm 2014); giá trị hàng tịch thu chưa bán 133,8 tỷ đồng (tăng 44,5 tỷ đồng, tăng 59,5% so với năm 2014).\r\nĐối với công tác buôn lậu thuốc lá, Phó Thủ tướng Chính phủ Nguyễn Xuân Phúc – Trưởng ban Chỉ đạo 389 (BCĐ 389) quốc gia (Ban Chỉ đạo quốc gia chống buôn lậu, gian lận thương mại và hàng giả) đã hoan nghênh và biểu dương các Bộ, ngành, lực lượng chức năng và các địa phương đã nghiêm túc triển khai có hiệu quả Chỉ thị số 30/CT-TTg của Thủ tướng Chính phủ. Kết quả công tác chống buôn lậu thuốc lá đã giảm 30% lượng thuốc lá nhập lậu, trong đó lực lượng QLTT đã góp phần bắt giữ trên 1,9 triệu bao.\r\n\r\nCông tác phổ biến tuyên truyền pháp luật thực hiện tốt. Tổ chức ký cam kết hàng trăm nghìn đối tượng kinh doanh tập trung vào cơ sở kinh doanh mặt hàng thuốc lá, phân bón, mũ bảo hiểm, các siêu thị, trung tâm thương mại... Hoạt động kiểm tra việc chấp hành các nội dung ký cam kết được lực lượng QLTT quan tâm kiểm tra.', 'pho-thu-tuong-co-quan-ly-thi-truong-bao-ke-cho-hang-gia', '', 1, '', '0000-00-00 00:00:00', '0000-00-00 00:00:00', 1, NULL, NULL),
(2, '        Vô Song Kiếm - Game Việt ấn định ra mắt vào 12/01\n\n', '        Vô Song Kiếm đã sẵn sàng để ra mắt phiên bản chính thức vào 12/01 tới. Được biết, đây là game mobile mới nhất của Studio Việt GameVN và NPH Vega Game.\r\n\r\n', '<p>Su khi kết th&uacute;c giai đoạn Alpha Test, V&ocirc; Song Kiếm đ&atilde; sẵn s&agrave;ng để ra mắt phi&ecirc;n bản ch&iacute;nh thức v&agrave;o 12/01 tới. Được biết, đ&acirc;y l&agrave; game mobile mới nhất của Studio Việt GameVN v&agrave; NPH Vega Game. Với những fan h&acirc;m mộ bộ truyện tranh 7 Vi&ecirc;n ngọc rồng, c&oacute; lẽ Lưỡng Long Nhất Thể l&agrave; tuyệt kỹ ấn tượng thứ hai chỉ đứng sau chưởng lực Kamejoko (Kamehameha) của khỉ con Songoku. Lấy &yacute; tưởng từ tuyệt chi&ecirc;u n&agrave;y, đội ngũ ph&aacute;t triển của GameVN đ&atilde; x&acirc;y dựng một phi&ecirc;n bản game Kim Dung với t&iacute;nh năng Song Kiếm Hợp B&iacute;ch độc đ&aacute;o: 2 hoặc 3 nh&acirc;n vật c&oacute; mối lương duy&ecirc;n chuẩn x&aacute;c với nhau khi ra trận c&oacute; thể c&ugrave;ng hợp thể v&agrave; tung ra một tuyệt kỹ cực mạnh. Đ&ograve;n combo đầy uy lực n&agrave;y c&oacute; thể thổi bay to&agrave;n bộ đội h&igrave;nh địch, với điều kiện nh&acirc;n vật phải được k&iacute;ch đầy thanh nộ kh&iacute;. Được biết, đ&ograve;n combo mạnh nhất game đang thuộc sở hữu của 3 huynh đệ Kiều Phong - Đo&agrave;n Dự - Hư Tr&uacute;c mang t&ecirc;n Quần Long Chi Thủ. Một ưu điểm kh&ocirc;ng thể bỏ qua của V&ocirc; Song Kiếm l&agrave; mỗi nh&acirc;n vật v&agrave; chi&ecirc;u thức của game đều được lồng tiếng rất nghi&ecirc;m t&uacute;c, đ&uacute;ng chất v&otilde; l&acirc;m kiếm hiệp. Sở dĩ để l&agrave;m được điều n&agrave;y, V&ocirc; Song Kiếm đ&atilde; phải thu&ecirc; lại đội ngũ chuy&ecirc;n lồng tiếng phim kiếm hiệp truyền h&igrave;nh để c&oacute; được chất giọng chuẩn v&agrave; quen thuộc nhất cho người nghe. C&aacute;c nh&acirc;n vật trong game cũng rất đa dạng phong ph&uacute;, từ Thần Đi&ecirc;u Đại Hiệp, Thi&ecirc;n Long B&aacute;t Bộ... đến những nh&acirc;n vật của Lộc Đỉnh K&yacute;, Hiệp Kh&aacute;ch H&agrave;nh đều được xuất hiện v&agrave; thể hiện hết sức sắc n&eacute;t.</p>\r\n', '-vo-song-kiem-game-viet-an-dinh-ra-mat-vao-1201-', '', 2, '        ', '0000-00-00 00:00:00', '2016-01-29 09:54:30', 0, NULL, NULL),
(3, 'Những game online bắn súng có đồ họa đẹp đến ngỡ ngàng', 'Tất cả những tựa game online bắn súng này đều xứng đáng thử qua trong thời gian ngắn tới đây', '<strong>Firefall</strong>\n\n<strong><a href="http://gamek.vn/firefall.htm" target="_blank" title="Firefall">Firefall</a></strong>&nbsp;l&agrave; một&nbsp;<a href="http://gamek.vn/game-ban-sung.htm" target="_blank" title="game bắn súng">game bắn s&uacute;ng</a>&nbsp;kết hợp nhập vai c&oacute; cả g&oacute;c nh&igrave;n thứ 1 lẫn thứ 3. Game sở hữu đồ họa b&oacute;ng bẩy v&agrave; nhiều t&iacute;nh năng đột ph&aacute; như thế giới mở chứ kh&ocirc;ng g&oacute;i gọn theo h&igrave;nh thức room truyền thống.</p>\n\n<strong>Firefall</strong>&nbsp;lấy cốt truyện khoa học viễn tưởng trong tương lai, khi m&agrave; người Tr&aacute;i Đất bắt đầu kh&aacute;m ph&aacute; vũ trụ, họ ph&aacute;t hiện ra những h&agrave;nh tinh mới, nhiều tiềm năng nhưng cũng phải đối mặt với lũ qu&aacute;i vật đang ngự trị tr&ecirc;n h&agrave;nh tinh n&agrave;y.\n', 'nhung-game-online-ban-sung-co-do-hoa-dep-den-ngo-ngang', '', 2, 'gamek', '0000-00-00 00:00:00', '0000-00-00 00:00:00', 1, 1, '2016-01-18 21:40:30'),
(4, 'Sợ cày kéo, muốn giữ sức khỏe, game thủ Việt chơi game online nào?', 'Cùng chúng tôi điểm qua những tựa game online nhẹ nhàng không đòi hỏi cày kéo mệt mỏi trong những buổi tối sau khi công việc và học tập đã được gác qua một bên', '<p>Như vậy l&agrave; 37Games đ&atilde; ch&iacute;nh thức mở cửa rộng r&atilde;i cho game online nhập vai h&agrave;nh động nền web&nbsp;<strong><a href="http://gamek.vn/guardians-of-divinity.htm" target="_blank" title="Guardians of Divinity">Guardians of Divinity</a></strong>&nbsp;sau hơn chục ng&agrave;y thử nghiệm v&agrave; nhận được nhiều phản ứng t&iacute;ch cực của cộng đồng game thủ to&agrave;n thế giới. Chi tiết cũng như th&ocirc;ng tin đăng k&yacute; t&agrave;i khoản game thủ c&oacute; thể t&igrave;m hiểu tại trang web của tựa game tại địa chỉ&nbsp;<a href="http://news.37.com/god/cover?cid=54&amp;scid=godc" rel="nofollow" target="_blank" title="http://news.37.com/god/cover?cid=54&amp;scid=godc">http://news.37.com/god/cover?cid=54&amp;scid=godc</a></p>\r\n\r\n<p>Được biết tr&ograve; chơi được ph&aacute;t h&agrave;nh dưới dạng miễn ph&iacute; giờ chơi ho&agrave;n to&agrave;n, game thủ c&oacute; thể dễ d&agrave;ng tham gia chơi m&agrave; kh&ocirc;ng cần phải lo lắng về việc mất tiền để được chơi.</p>\r\n\r\n<p><strong><a href="http://gamek.vn/game-online/game-online-arpg-hap-dan-guardians-of-divinity-mo-cua-thu-nghiem-20160107150627282.chn" target="_blank" title="Guardians of Divinity">Guardians of Divinity</a></strong>&nbsp;lấy bối cảnh &quot;phong thần viễn cổ&quot; đ&uacute;ng nghĩa đen, nhưng trong game bạn sẽ v&agrave;o vai những nh&acirc;n vật dựa tr&ecirc;n truyền thuyết Hy Lạp, Trung Quốc v&agrave; cả Ai Cập cổ đại chứ kh&ocirc;ng ho&agrave;n to&agrave;n phụ thuộc v&agrave;o bối cảnh phương Đ&ocirc;ng như trước nữa.</p>\r\n', 'so-cay-keo-muon-giu-suc-khoe-game-thu-viet-choi-game-online-nao', '', 2, 'gamek', '0000-00-00 00:00:00', '0000-00-00 00:00:00', 1, 1, '2016-01-19 10:35:17'),
(5, 'Ấn tượng trược bộ case máy tính Ghost Rider cực chất', 'Modder người Thái Lan có biệt danh CROW mới đây vừa cho ra mắt phiên bản case máy tính độ vô cùng độc đáo mang tên "TJ 07 Ghost Rider".', '<p>Modder người Th&aacute;i Lan c&oacute; biệt danh CROW mới đ&acirc;y vừa cho ra mắt phi&ecirc;n bản case&nbsp;<a draggable="false" href="http://gamek.vn/may-tinh.htm" target="_blank" title="máy tính">m&aacute;y t&iacute;nh</a>&nbsp;độ v&ocirc; c&ugrave;ng độc đ&aacute;o mang t&ecirc;n &quot;TJ 07 Ghost Rider&quot;. Phi&ecirc;n bản case độ n&agrave;y c&oacute; sức h&uacute;t kh&oacute; cưỡng nổi với người xem v&igrave; vẻ ngo&agrave;i hầm hố v&agrave; bộ ống xả to&aacute;t l&ecirc;n vẻ cơ kh&iacute; độc đ&aacute;o v&agrave; mạnh mẽ.</p>\r\n\r\n<p>Modder CROW vốn đ&atilde; nổi tiếng với nhiều bản độ trước đ&acirc;y m&agrave; cụ thể l&agrave; bản độ mang t&ecirc;n Differ DP-3 đ&atilde; từng rất nổi tiếng. V&agrave; nay anh lại cho ra mắt th&ecirc;m một phi&ecirc;n bản case độ tiếp theo lấy &yacute; tưởng từ bộ phim &quot;Ghost Rider&quot; m&agrave; PCNews tạm dịch l&agrave; &quot;Ma Tốc Độ&quot;. Với một chiếc đầu l&acirc;u rực lửa ở mặt trước c&ugrave;ng bốn ống xả l&agrave;m bằng hợp kim s&aacute;ng b&oacute;ng ph&iacute;a trước mặt, n&oacute; to&aacute;t l&ecirc;n một vẻ hầm hố của một chiếc xe ph&acirc;n khối lớn được điều khiển bởi một con quỷ dữ c&oacute; chiếc đầu rực lửa đ&uacute;ng như trong bộ phim &quot;Ghost Rider&quot;.</p>\r\n\r\n<p>Bộ case n&agrave;y được t&agrave;i trợ bởi Asus Thailand, h&atilde;ng đ&atilde; từng t&agrave;i trợ trong rất nhiều bản case độ của CROW. Hiện tại chưa c&oacute; cấu h&igrave;nh ch&iacute;nh thức của chiếc case độ n&agrave;y, song qua những h&igrave;nh ảnh dưới đ&acirc;y người xem cũng c&oacute; thể h&igrave;nh dung ẩn chứa b&ecirc;n trong vẻ bề ngo&agrave;i &quot;quỷ dữ&quot; n&agrave;y l&agrave; những linh kiện cũng dữ dằn kh&ocirc;ng k&eacute;m.</p>\r\n', 'an-tuong-truoc-bo-case-may-tinh-ghost-rider-cuc-chat', '', 2, 'gamk', '0000-00-00 00:00:00', '0000-00-00 00:00:00', 1, NULL, '2016-01-19 15:31:33'),
(6, 'TS Nguyễn Bá Hải - Nhà khoa học "điên" với chiếc kính "mắt thần" chỉ đường cho người khiếm thị', 'Lặng lẽ nghiên cứu, sáng tạo và đi khắp các tỉnh thành trao kính "mắt thần" cho người khiếm thị, có lẽ TS Nguyễn Bá Hải sẽ vẫn chỉ là một nhà khoa học thầm lặng cho đến khi anh có 10 phút đối thoại cùng Thủ tướng Nguyễn Tấn Dũng để thuyết phục Thủ tướng đầu tư vào dự án triệu đô của mình.', '<p>T&ocirc;i biết&nbsp;TS Nguyễn B&aacute; Hải&nbsp;từ nhiều năm trước, khi anh đ&atilde; ho&agrave;n th&agrave;nh kh&oacute;a học ở H&agrave;n Quốc v&agrave; trở về l&agrave;m Ph&oacute; bộ m&ocirc;n&nbsp;Điện - điện tử &ocirc; t&ocirc; của trường Đại học Sư phạm Kỹ thuật TP. HCM. Người giảng vi&ecirc;n năm ấy thường chia sẻ mọi cung bậc cảm x&uacute;c tr&ecirc;n facebook, khi h&acirc;n hoan, hớn hở v&igrave; c&ugrave;ng c&aacute;c sinh vi&ecirc;n v&agrave; cộng sự tạo ra một sản phẩm n&agrave;o đ&oacute;, khi th&igrave; trăn trở cho cuộc sống của người khiếm thị, người ngh&egrave;o ở Việt Nam. Tuy nhi&ecirc;n, nhũng h&igrave;nh ảnh v&agrave; b&agrave;i viết đa phần xoay quanh m&aacute;y m&oacute;c, chế tạo qu&aacute; xa lạ l&agrave;m t&ocirc;i cũng kh&ocirc;ng c&ograve;n để &yacute; nhiều đến nh&agrave; khoa học ấy nữa, cho đến một h&ocirc;m, anh gửi lời k&ecirc;u gọi mọi người gi&uacute;p m&igrave;nh t&igrave;m một người m&ugrave; h&aacute;t rong tr&ecirc;n phố.</p>\r\n\r\n<p>Lời k&ecirc;u gọi ấy khiến t&ocirc;i bắt đầu theo d&otilde;i anh nhiều hơn, qua b&agrave;i chia sẻ, anh n&oacute;i rằng anh xem một đoạn clip về ch&agrave;ng trai m&ugrave; h&aacute;t rong kiếm ăn tr&ecirc;n đường phố S&agrave;i G&ograve;n v&agrave; muốn t&igrave;m gặp người n&agrave;y để tặng anh ấy một chiếc k&iacute;nh đặc biệt. Mọi người kh&ocirc;ng biết chuyện g&igrave; xảy ra cho đến một thời gian sau, anh Hải chia sẻ một đoạn clip với nội dung: T&ocirc;i đ&atilde; gặp người m&ugrave; h&aacute;t rong n&agrave;y v&agrave; tặng k&iacute;nh &quot;Mắt thần&quot; cho anh ấy.</p>\r\n', 'ts-nguyen-ba-hai-nha-khoa-hoc-dien-voi-chiec-kinh-mat-than-chi-duong-cho-nguoi-khiem-thi', '', 4, 'genk', '0000-00-00 00:00:00', '0000-00-00 00:00:00', 1, NULL, '2016-01-19 15:40:38'),
